import {useState} from "react";
import Card from "./Card";

const List = ({name, index}) => {
  const [cards, set_cards] = useState([]);

  const list_style = {
    borderRadius: "10px",
    fontSize: "1.5vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    
  }
  // border-radius: 10px;
  // font-size: 1.5vh;
  // display: flex;
  // flex-direction: column;
  // align-items: center;
  // justify-content: center;
  // gap: 0.6rem;
  // padding: 0.6rem;
  // background-color: #dedede;



  const add_card = () => {
    set_cards([...cards, {name: "Hello", description: "gerge"}]);
  }
  return (
    <div className="board" id={`list-${index}`}>
      <div id="board-header-container">
        <div id="board-name">{name}</div>
        <button id="board-functionalities">...</button>
      </div>
      {cards.map((card, index) => {
        return <Card name={card.name} description={card.description} key={index} />
      })}
      <div id="add-card" onClick={add_card}>+ Add card</div>
    </div>
  );
};

export default List;
