import {useRef} from "react"

const profile_overlay = ({set_profile_vis, profile_vis}) => {

  const handleClickOutside = (e) => {
    if (profile_overlay_ref.current && !profile_overlay_ref.current.contains(e.target)) {
      set_profile_vis("hidden");
    }
  };
  document.addEventListener("click", handleClickOutside);

  return (
    <div id="profile-overlay" style={{visibility: profile_vis}} >
      <div id="profile-header">
        <div id="profile-picture"></div>
        <div id="profile-name">Name</div>
      </div>
    </div>
  );
};

export default profile_overlay;
