import { useContext, useRef, useEffect } from "react";
import { Board_Id_Context } from "C:/Users/alexi/Downloads/VsCode Projects/Wubo (Health Website)/Health-Website/my-app/src/App";

const Name_of_Board = () => {
  const board_id = useContext(Board_Id_Context);
  const ref = useRef(null)

  const change_board_name = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      //get the value inside the span
      const new_board_name = e.target.innerText;
      console.log(new_board_name);
    }
  }

  useEffect(() => {
  
  
  

    const handleClickOutside = (e) => {
      if (ref.current && !ref.current.contains(e.target)) {
        setVisibilityState("visible");
        setVisibilityState_input("hidden");
      }
    }

    document.addEventListener("click", handleClickOutside);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  },[]);




  if (
    board_id.multiple_board_info.length === 0 ||
    board_id.single_board_info.length === 0
  ) {
    return <div id="name-of-board">Loading...</div>;
  }

  return (
    <>
    <div id="name-of-board">
      <span ref={ref} contentEditable="true" suppressContentEditableWarning={true} onKeyDown={change_board_name}>{board_id.single_board_info[0].name}</span>
    </div>
    </>
  );
};

export default Name_of_Board;
