import Name_of_Board from "./Name_of_Board";
import Favorite_Button from "./Favorite_Button";
import { useContext, useRef, useEffect} from "react";
import { Board_Id_Context } from "C:/Users/alexi/Downloads/VsCode Projects/Wubo (Health Website)/Health-Website/my-app/src/App";

const Board_Header_2 = ({ Set_Side_Menu_Visibility, move_content_to_right, set_move_content_to_right}) => {

  //side menu button
  const open_side_menu = () => {
    Set_Side_Menu_Visibility("visible");
    set_move_content_to_right("28vh");
  }

  return (
    <div id="header-2" style={{left: move_content_to_right}}>
      <button className={"btn btn--secondary--solid btn--small"} id="side-menu-button" onClick={open_side_menu}>-</button>
      <div id="name-of-board" ref={ref}>
  <span
    ref={board_name}
    id="span-of-name-of-board"
    contentEditable="true"
    suppressContentEditableWarning={true}
    onKeyDown={change_board_name}
    spellCheck={false}
  >
    {board_id.single_board_info[0].name}
  </span>
</div>
      <Favorite_Button />
    </div>
  );
};

export default Board_Header_2;
