import { useNavigate } from "react-router-dom";
import {useAuth} from "C:/Users/alexi/Downloads/VsCode Projects/Wubo (Health Website)/Health-Website/my-app/src/context/AuthContext";
import {useRef, useState} from "react";
import {getAuth } from "firebase/auth";


const Login = () => {
  const navigate = useNavigate();

  const emailRef = useRef();
  const passwordRef = useRef();
  const comfirmPasswordRef = useRef();
  const {signup} = useAuth();
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  const auth = getAuth();

  async function handleSubmit(e) {
    e.preventDefault();
    if(passwordRef.current.value !== comfirmPasswordRef.current.value) {
      return setError("Passwords do not match");
    }
    try {
      setError("");
      setLoading(true);
      
      await signup(auth, emailRef.current.value, passwordRef.current.value);
    } catch (error) {
      console.log(error);
      setError("Failed to create an account");
    }
    setLoading(false);
    
  }


  const go_to_sign_up_page = () => {
    navigate("/sign-up");
  };
  return (
    <form id="main-login-container" onSubmit={handleSubmit}>
      <h1 id="login-title">Login</h1>
      <div id="email-container">
        <div id="email-title">Email:</div>
        <input id="email-input" type="text" />
      </div>
      <div id="password-container">
        <div id="password-title">Password:</div>
        <input id="password-input" type="password" />
      </div>
      <button id="login-button">login</button>
      <div id="forgot-password-container">Forgot Email/Password?</div>
      <div id="create-account-container" onClick={go_to_sign_up_page}>
        Create your account
      </div>
    </form>
  );
};

export default Login;
