import {useState, useRef} from 'react'

const Create_List = () => {
  const [list_visibility, set_list_visibility] = useState("hidden");
  const [list_name, set_list_name] = useState("");
  const create_list = useRef(null);
  const create_list_overlay = useRef(null);
  const create_board_list = () => {
    set_list_visibility("visible")
  };

  const handleClickOutside = (e) => {
    if (create_list_overlay.current && !create_list_overlay.current.contains(e.target) && !create_list.current.contains(e.target)) {
      set_list_visibility("hidden")
    }
  }
  document.addEventListener("click", handleClickOutside);

  const create_list_component = () => {
    return (
      <div className="create_list_overlay" ref={create_list_overlay}>
        <div className="create_list" ref={create_list}>
          <div className="create_list_header">
            <h3>Create List</h3>
            <button className="close_button" onClick={() => set_list_visibility("hidden")}>X</button>
          </div>
          <div className="create_list_body">
            <input type="text" placeholder="List Name" value={list_name} onChange={(e) => set_list_name(e.target.value)}/>
            <button className="create_list_button" onClick={() => set_list_visibility("hidden")}>Create</button>
          </div>
        </div>
      </div>
    )
  }


  return (
    <>
      <div id="create-list" onClick={create_board_list} ref={create_list}>
        + create list
      </div>
      <div id="create-list-overlay" style={{visibility: list_visibility}} ref={create_list_overlay}>
        <input
          className="create-list-input"
          type="text"
          placeholder="Enter list title"
          value={list_name}
          onChange={(e) => set_list_name(e.target.value)}
        />
        <button id="create-list-button" onClick={create_list_component}>Create</button>
      </div>
      <div id="board">
        <div id="board-header-container">
          <div id="board-name">Board Title</div>
          <button id="board-functionalities">...</button>
        </div>
        <div id="add-card">+ Add card</div>
      </div>
    </>
  );
};

export default Create_List;
